        etMail = findViewById(R.id.etMail);

        etPassword = findViewById(R.id.etPassword);

        btnLogin = findViewById(R.id.btnLogin);
        btnLogin.setOnClickListener(v -> {
            try {
                if (etMail.getText().toString().isEmpty() && etPassword.getText().toString().isEmpty()) {
                    Toast.makeText(this, "Ingrese datos", Toast.LENGTH_SHORT).show();
                } else {
                    if (userDB.authUser(etMail.getText().toString(), etPassword.getText().toString())) {
                        Intent intent = new Intent(getApplicationContext(), HomeActivity.class);
                        Log.i(String.valueOf(userDB.userLogged.toString()),"");
                        startActivity(intent);
                    } else {
                        Toast.makeText(this, "Correo y/o contraseña Incorrectos", Toast.LENGTH_SHORT).show();
                    }
                    etMail.setText("");
                    etPassword.setText("");
                    etMail.findFocus();
                }
            } catch (SQLException e) {
                e.printStackTrace();
            }
        });

